def create\n  @comment = Comment.new(comment_params)\n\n  respond_to do |format|\n    if @comment.save\n      flash.now[:notice] = 'Added comment.' # Use flash.now to avoid persistence in session\n      format.js { render :create }        # Render the create.js.erb template\n    else\n      format.js { render json: @comment.errors.full_messages, status: :unprocessable_entity }\n    end\n  end\nend