class User < ApplicationRecord\n  validates :email, presence: true, format: { with: URI::MailTo::EMAIL_REGEXP }\n  validates :username, presence: true, uniqueness: true\nend\n\n# Accessing validations programmatically\nUser.validators.each do |validator|\n  if validator.is_a?(ActiveModel::Validations::PresenceValidator)\n    puts "Attribute '#{validator.attributes.first}' must be present."\n  elsif validator.is_a?(ActiveModel::Validations::FormatValidator)\n    puts "Attribute '#{validator.attributes.first}' must match the format: /#{validator.options[:with]}/"\n  elsif validator.is_a?(ActiveModel::Validations::UniquenessValidator)\n    puts "Attribute '#{validator.attributes.first}' must be unique."\n  end\nend