# app.rb\nrequire 'sinatra'\nrequire 'net/smtp'\n\npost '/contact' do\n  # Extract form data\n  name = params[:name]\n  email = params[:email]\n  message = params[:message]\n\n  # Prepare and send email\n  from_alias = "#{name} <#{email}>"\n  to = "owner@example.com"\n  subject = "New Contact Form Submission"\n\n  begin\n    Net::SMTP.start('localhost', 25) do |smtp|\n      smtp.send_message <<-END_OF_MESSAGE, 'noreply@yourdomain.com', to\nFrom: #{from_alias}\nTo: #{to}\nSubject: #{subject}\n\n#{message}\nEND_OF_MESSAGE\n    end\n\n    # Redirect to confirmation page with success message\n    redirect '/confirmation.html?status=success'\n  rescue StandardError => e\n    # Handle error and redirect with failure message\n    redirect "/confirmation.html?status=failure&error=#{e.message}"\n  end\nend