def run_and_capture(command)\n  puts "Executing: #{command}"\n  output = `#{command}`\n  if $? == 0\n    puts "Command executed successfully. Output:\n#{output}"\n  else\n    puts "Command failed with error code #{$?.exitstatus}. Output:\n#{output}"\n  end\nend\n\n# SVN update command\nrun_and_capture('svn update /path/to/working/copy')\n\n# File copy command using cp (Unix-like systems)\nrun_and_capture('cp -r /source/directory/* /destination/directory/')