#!/usr/bin/ruby\n\nrequire 'net/smtp'\n\ndef send_email(to, subject = "", body = "")\n    from = "my@email.com"\n    message = <<~EOF\n      From: #{from}\n      To: #{to}\n      Subject: #{subject}\n\n      #{body}\n\n    EOF\n\n    Net::SMTP.start('192.168.10.213', 25, '192.168.0.218') do |smtp|\n        smtp.send_message(message, from, to)\n    end\nend\n\nsend_email("my@email.com", "test", "blah blah blah")