@results = []\n\n# Fetching notes sorted by updated_at\nNote.order('updated_at DESC').limit(3).each do |item|\n  @results << item\nend\n\n# Fetching pictures sorted by updated_at\nPicture.order('updated_at DESC').limit(3).each do |item|\n  @results << item\nend\n\n# Sorting the combined results by updated_at (already sorted in descending order)\n@results.sort_by! { |result| -result.updated_at.to_i }