require 'combine_pdf'\n\n# Get all pdf file names in the current directory sorted\npdf_files = Dir.glob('[0-9][0-9]_*').sort\n\n# Create a new CombinePDF document\ncombined_pdf = CombinePDF.new\n\n# Add each pdf file's pages to the combined_pdf document\npdf_files.each do |file|\n  combined_pdf << CombinePDF.load(file)\nend\n\n# Save the combined pdf document to a new file\nFile.open('combined.pdf', 'wb') { |f| f.write(combined_pdf.to_pdf) }