class StoriesController < ApplicationController\n  def index\n    @stories = Story.all\n  end\n\n  def show\n    @story = Story.find(params[:id])\n  end\n\n  def new\n    @story = Story.new\n  end\n\n  def create\n    @story = Story.new(story_params)\n    if @story.save\n      redirect_to @story\n    else\n      render :action => "new"\n    end\n  end\n\n  def edit\n    @story = Story.find(params[:id])\n  end\n\n  def update\n    @story = Story.find(params[:id])\n    if @story.update_attributes(story_params)\n      redirect_to @story\n    else\n      render :action => "edit"\n    end\n  end\n\n  def destroy\n    @story = Story.find(params[:id])\n    @story.destroy\n    redirect_to stories_url\n  end\n\n  private\n\n  def story_params\n    params.require(:story).permit(:title, :body)\n  end\nend