class YourResourceController < ApplicationController\n  # ... other actions ...\n\n  def restore\n    @your_resource = TrashTable.find_by(your_resource_id: params[:id])\n    if @your_resource&.restore!\n      redirect_to your_resource_path(@your_resource), notice: 'Your resource has been restored.'\n    else\n      redirect_back fallback_location: root_path, alert: 'Failed to restore your resource.'\n    end\n  end\n\n  # ... other actions ...\nend