# app/policies/user_policy.rb\n   class UserPolicy\n     attr_reader :user, :target_user\n\n     def initialize(user, target_user)\n       @user = user\n       @target_user = target_user\n     end\n\n     def suspend?\n       @user.moderator? && @user != @target_user\n     end\n\n     def update?\n       if @user.admin?\n         true\n       elsif @user.moderator? && (@user == @target_user || some_other_business_logic?)\n         true\n       else\n         false\n       end\n     end\n   end