def calibration_constant\n  puts "Coefficient: #{self.coefficient}, Exponent: #{self.exponent}"\n  big_decimal = self.coefficient * (10**self.exponent)\n  result = big_decimal.to_f\n  puts "Result: #{result}"\n  return result\nend\n\n# In your test:\nputs "Expected: #{61.0.to_f}, Actual: #{calibrations[0].calibration_constant}"\nassert_equal(61.0.to_f, calibrations[0].calibration_constant)