# app/policies/comment_policy.rb\n   class CommentPolicy\n     attr_reader :user, :comment\n\n     def initialize(user, comment)\n       @user = user\n       @comment = comment\n     end\n\n     def update?\n       if @user.admin?\n         true\n       elsif @user.moderator? && (@comment.status.in?(%w[moderator normal]) || @user == @comment.user)\n         true\n       else\n         false\n       end\n     end\n\n     def create?\n       # Define creation rules\n     end\n\n     # Add other methods as needed\n   end