<!DOCTYPE html>\n    <html>\n    <head>\n        <title>Items</title>\n    </head>\n    <body>\n        <h1>Items</h1>\n        <ul>\n            <% @items.each do |item| %>\n                <li><%= item.name %> - <%= item.value %></li>\n            <% end %>\n        </ul>\n\n        <form action="/items" method="post">\n            Name: <input type="text" name="name"><br/>\n            Value: <input type="number" name="value"><br/>\n            <input type="submit" value="Create Item"/>\n        </form>\n    </body>\n    </html>