require 'win32/service'\n    include Win32\n\n    class RedmineService < Service\n      def initialize\n        super('Redmine', {:display_name => "Redmine Application", :description => "Runs the Redmine Rails application"})\n      end\n\n      def start_service\n        @thread = Thread.new do\n          system("rails server -b 0.0.0.0 -p 3000")\n        end\n      end\n\n      def stop_service\n        Process.kill 'TERM', @thread.pid if @thread && !@thread.stop?\n      end\n    end\n\n    RedmineService.new.run!