# Example of a simple Rails controller action to confirm an order\n     class OrdersController < ApplicationController\n       def create\n         @order = Order.new(order_params)\n         if @order.save\n           redirect_to order_path(@order), notice: 'Order was successfully placed.'\n         else\n           render :new, status: :unprocessable_entity\n         end\n       end\n\n       private\n\n       def order_params\n         params.require(:order).permit(:product_id, :quantity, :user_id)\n       end\n     end