require 'csv'\n\nclass EntriesController < ApplicationController\n  def getcsv\n    @entries = Entry.all\n\n    respond_to do |format|\n      format.csv { send_data generate_csv(@entries), filename: "entries_#{Date.today}.csv" }\n    end\n  end\n\n  private\n\n  def generate_csv(entries)\n    CSV.generate(headers: true) do |csv|\n      csv << %w[First_Name Last_Name] # Add other headers if necessary\n\n      entries.each do |entry|\n        csv << [entry.firstName, entry.lastName] # Add more fields as necessary\n      end\n    end\n  end\nend