# app/controllers/elements_controller.rb\nclass ElementsController < ApplicationController\n  def show\n    @element = Element.find(params[:id])\n    respond_to do |format|\n      format.ppt {\n        content = render_to_string(:template => "elements/show.ppt.builder")\n        send_data content, type: 'application/vnd.openxmlformats-officedocument.presentationml.presentation',\n                          filename: "#{@element.name}.ppt",\n                          disposition: 'attachment'\n      }\n    end\n  end\nend