require 'css_parser'\n\n   def validate_css(file_path)\n     begin\n       parser = CssParser::Parser.new\n       parser.load_file!(file_path)\n\n       if parser.errors.any?\n         puts "Errors in #{file_path}:"\n         parser.each_error { |e| puts e }\n       else\n         puts "#{file_path} is valid."\n       end\n     rescue => e\n       puts "Error reading file: #{e}"\n     end\n   end\n\n   validate_css('path/to/your/css_file.css')