require 'webrick'\n\nserver = WEBrick::HTTPServer.new(\n  :Port            => 3344,\n  :BindAddress     => "127.0.0.1", # Bind only to localhost\n  # Other configuration...\n)\n\nserver.mount("/", WEBrick::HTTPServlet::ProcHandler.new do |req, res|\n  puts "Access from: #{req.remote_ip}"\n  if req.remote_ip != "127.0.0.1"\n    res.status = 403\n    res.body = "Access Denied"\n  else\n    res.body = "Hello, localhost!"\n  end\nend)\n\ntrap("INT") { server.shutdown } # Allow graceful shutdown\n\nserver.start