require 'nokogiri'\n\n   def validate_xhtml(file_path)\n     begin\n       doc = Nokogiri::XML(File.read(file_path), external_ent: false) do |config|\n         config.dtdload.dtdvalid.noblanks.ng\n       end\n\n       if doc.internal_subset && !doc.errors.empty?\n         puts "Errors in #{file_path}:"\n         doc.errors.each { |e| puts e }\n       else\n         puts "#{file_path} is well-formed and valid."\n       end\n     rescue => e\n       puts "Error reading file: #{e}"\n     end\n   end\n\n   validate_xhtml('path/to/your/xhtml_file.xhtml')