module ApplicationHelper\n  def generate_table(items, columns)\n    content_tag(:table) do\n      concat(content_tag(:thead) do\n        concat(content_tag(:tr) { columns.each { |c| concat(content_tag(:th, c.to_s.humanize)) } })\n      end)\n      concat(content_tag(:tbody) do\n        items.each do |item|\n          concat(content_tag(:tr) { columns.each { |col| concat(content_tag(:td, item.send(col))) } })\n        end\n      end)\n    end\n  end\n\n  def generate_form(form_builder, attributes)\n    form_builder.fields_for :model do |fb|\n      attributes.each do |attr|\n        fb.label(attr)\n        fb.text_field(attr) # You can customize based on field type\n        concat fb.submit("Submit")\n      end\n    end\n  end\nend