require 'open-uri'\n   require 'openssl'\n\n   def get_blogs\n     uri = URI.parse("https://someInternalWebSite/weblogs/feed/entries/atom")\n     # Load custom CA certificate or disable verification\n     cert_store = OpenSSL::X509::Store.new.tap { |store| store.add_file('/path/to/custom/ca_certificate.pem') }\n     response = open(uri, ssl_verify_mode: OpenSSL::SSL::VERIFY_PEER, ssl_ca_cert_store: cert_store)\n     doc = REXML::Document.new(response.read)\n     blogs = Array.new\n     # ----- some crap to parse the blogs\n     return blogs\n   end