require 'open-uri'\nrequire 'nokogiri'\n\nurl = 'http://feeds.feedburner.com/RidingRails'\n\n# Explicitly parse as XML\nxml_doc = Nokogiri::XML(URI.open(url))\nputs "Explicitly parsed XML: #{xml_doc.class}"\n\n# Use search or xpath on the explicitly parsed XML document\nputs "XPath count for feed/entry: #{xml_doc.xpath('/feed/entry').size}"\nputs "Search count for feed entry: #{xml_doc.search('feed entry').size}"\n\n# If you need to handle HTML, you can parse it similarly\nhtml_url = 'http://weblog.rubyonrails.org/'\nhtml_doc = Nokogiri::HTML(URI.open(html_url))\nputs "Explicitly parsed HTML: #{html_doc.class}"