def validate_field\n     field = params.keys.first\n     value = params[field]\n     @resource = Resource.new(field => value)\n     valid = @resource.valid?(field.to_sym)\n\n     respond_to do |format|\n       format.json {\n         if valid\n           render json: { valid: true }\n         else\n           message = @resource.errors.full_messages_for(field).first || "Invalid #{field}"\n           render json: { valid: false, message: message, field_name: field }\n         end\n       }\n     end\n   end