require 'test/unit'\nrequire 'nokogiri'\n\nclass HtmlComparisonTest < Test::Unit::TestCase\n  def normalize_html(html)\n    doc = Nokogiri::HTML.fragment(html)\n    # Remove unnecessary whitespace and return the content as a string\n    doc.to_xpath.strip.gsub(/\s+/, ' ')\n  end\n\n  def test_html_output\n    expected_html = "  <div>  <p>Hello World</p></div>  "\n    actual_html   = "<div><p>Hello   World</p></div>"\n    \n    assert_equal normalize_html(expected_html), normalize_html(actual_html)\n  end\nend