class Admin::BannersController < ApplicationController\n  before_action :set_banner, only: [:show, :edit, :update, :destroy]\n\n  def index\n    @banners = Banner.all\n  end\n\n  def show\n  end\n\n  def new\n    @banner = Banner.new\n  end\n\n  def edit\n  end\n\n  def create\n    @banner = Banner.new(banner_params)\n    \n    if @banner.save\n      redirect_to admin_banners_path, notice: 'Banner was successfully created.'\n    else\n      render :new \n    end\n  end\n\n  def update\n    if @banner.update(banner_params)\n      redirect_to admin_banners_path, notice: 'Banner was successfully updated.'\n    else\n      render :edit \n    end\n  end\n\n  def destroy\n    @banner.destroy\n    redirect_to admin_banners_path, notice: 'Banner was successfully destroyed.'\n  end\n\n  private\n    def set_banner\n      @banner = Banner.find(params[:id])\n    end\n\n    def banner_params\n      params.require(:banner).permit(:name, :url, :image)\n    end\nend