def create\n     @resource = Resource.new(resource_params)\n     if @resource.valid?\n       @resource.save\n       respond_to do |format|\n         format.js { render template: 'create_success' }\n         format.json { render json: { success: true, message: "Resource created successfully" } }\n       end\n     else\n       respond_to do |format|\n         format.js { render template: 'create_failure' }\n         format.json { render json: @resource.errors.full_messages, status: :unprocessable_entity }\n       end\n     end\n   end