# app/controllers/class_memberships_controller.rb\nclass ClassMembershipsController < ApplicationController\n  before_action :set_student\n\n  def create\n    @student.classes << Class.find(params[:class_id])\n    redirect_to student_path(@student)\n  end\n\n  def destroy\n    @student.classes.delete(Class.find(params[:id]))\n    redirect_to student_path(@student)\n  end\n\n  private\n\n  def set_student\n    @student = Student.find(params[:student_id])\n  end\nend\n\n# app/controllers/student_memberships_controller.rb\nclass StudentMembershipsController < ApplicationController\n  before_action :set_class\n\n  def create\n    @klass.students << Student.find(params[:student_id])\n    redirect_to class_path(@klass)\n  end\n\n  def destroy\n    @klass.students.delete(Student.find(params[:id]))\n    redirect_to class_path(@klass)\n  end\n\n  private\n\n  def set_class\n    @klass = Class.find(params[:class_id])\n  end\nend