# app/lib/formattable_dates.rb\nmodule FormattableDates\n  def self.included(base)\n    base.class_eval do\n      after_initialize :generate_formatted_date_methods\n    end\n  end\n\n  private\n\n  def generate_formatted_date_methods\n    self.class.columns.each do |column|\n      if column.type == :datetime || column.type == :date\n        define_singleton_method("formatted_#{column.name}") do\n          send(column.name).present? ? send(column.name).to_s(:date) : nil\n        end\n      end\n    end\n  end\nend\n\n# app/models/your_model.rb\nclass YourModel < ApplicationRecord\n  include FormattableDates\nend