# lib/rack/content_type_fixer.rb\n   module Rack\n     class ContentTypeFixer\n       def initialize(app)\n         @app = app\n       end\n\n       def call(env)\n         status, headers, body = @app.call(env)\n         headers['Content-Type'] ||= 'text/html'\n         headers['Content-Type'] += '; charset=utf-8' unless headers['Content-Type'].include?('charset')\n         [status, headers, body]\n       end\n     end\n   end