# app/models/website.rb\nclass Website < ActiveRecord::Base\n  def to_param\n    # Encode the domain name for URI use\n    CGI.escape(domain_name)\n  end\nend\n\n# config/routes.rb\nmap.resources :websites, \n              :has_many => :dns_records,\n              :requirements => { :id => /[^\/]+/ }  # Match anything except /\n\nmap.resources :dns_records, \n              :belongs_to => :website,\n              :requirements => { :id => /[^\/]+/, :website_id => /[^\/]+/ }\n\n# Example usage in views\n<%= link_to "Show #{w}", website_path(w) %>