# On the client app, assume User is an ActiveResource model\nclass User < ActiveResource::Base\n  self.site = "http://auth.app"\n  \n  # Custom method to fetch associated contacts\n  def contacts\n    Contact.where(user_id: id)\n  end\n  \n  # Custom method to set up associations (e.g., adding a contact)\n  def add_contact(contact_attributes)\n    contact = Contact.new(contact_attributes.merge(user_id: id))\n    contact.save ? contact : false\n  end\nend\n\n# On the client app, assume Contact is an ActiveRecord model\nclass Contact < ApplicationRecord\n  # Assuming there's a user_id column in contacts table to make associations\n  belongs_to :user, foreign_key: :user_id, optional: true\nend