class ApplicationController < ActionController::Base\n  before_action :authenticate_request\n\n  protected\n\n  def authenticate_request\n    render plain: "Unauthorized", status: :unauthorized unless valid_request?\n  end\n\n  def valid_request?\n    api_key = ENV['API_KEY'] # Ensure this is set in your environment variables securely\n    request.headers['X-API-Key'] == api_key\n  end\nend