require 'net/imap'\nrequire 'date'\n\n imap = Net::IMAP.new('mail.xks.com')\n imap.login('web1_xk3', 'pxx2006')\n imap.select('INBOX')\n\nimap.search(["SENTSINCE", (Time.now - 1.day).strftime("%d-%b-%Y")]).each do |msgid|\n  # Fetch the email by its sequence number\n  mail = imap.fetch(msgid, "BODY[TEXT]")[0].attr["BODY[TEXT]"]\n\n  # Decode the body text if necessary. This is a simple example and assumes no encoding.\n  puts "Email Body: #{mail}"\nend\n\nimap.logout\nimap.disconnect