# app/controllers/analytics_controller.rb\nclass AnalyticsController < ApplicationController\n  def show\n    @analytics_service = AnalyticsService.new(start_date: params[:start_date], end_date: params[:end_date])\n    redirect_to new_analytics_path and return unless @analytics_service.start_date && @analytics_service.end_date\n    \n    @metrics = @analytics_service.metrics\n  end\nend