require 'net/http'\nrequire 'uri'\n\nurl = URI.parse('http://localhost:3000/someservice/')\nrequest = Net::HTTP::Post.new(url.path)\nrequest['Content-Type'] = 'application/xml'  # Set the Content-Type header to XML\nrequest.body = "<?xml version='1.0' encoding='UTF-8'?><somedata><name>Test Name 1</name><description>Some data for Unit testing</description></somedata>"\n\nresponse = Net::HTTP.start(url.host, url.port) do |http|\n  http.request(request)\nend\n\nputs response.code\nputs response.body\n\n# Check if the request was successful and data is persisted\nassert_equal '201', response.code