# In your ApplicationController (or specific controllers if needed)\nclass ApplicationController < ActionController::Base\n  rescue_from StandardError, with: :render_error\n\n  private\n\n  def render_error(exception)\n    respond_to do |format|\n      format.html { render template: 'errors/internal_server_error', status: 500 }\n      format.json { render json: { error: exception.message }, status: 500 }\n      format.js   { render js: "alert('An error occurred: #{exception.message}');", status: 500 }\n    end\n  end\nend