module Something\n  def self.included(base)\n    base.include(ActiveModel::Validations) # Include validations in the including class\n    base.class_eval do\n      validates :name, presence: true\n      validates :date, presence: true\n    end\n  end\nend\n\nmodule Anotherthing\n  def self.included(base)\n    base.include(ActiveModel::Validations) # Include validations in the including class\n    base.class_eval do\n      validates :age, presence: true\n    end\n  end\nend\n\nclass ADualClass\n   include Something\n   include Anotherthing\nend