require 'resolv'\n\nresolver = Resolv::DNS.new\nbegin\n  resolver.each_address('example.com') do |address|\n    puts "IP: #{address}"\n  end\n# For MX records specifically:\n  mx_records = resolver.getresources('example.com', Resolv::DNS::Resource::IN::MX)\n  mx_records.each { |mx_record| puts "Mail server: #{mx_record.exchange.to_s}, Priority: #{mx_record.preference}" }\nrescue StandardError => e\n  puts "No DNS records found or an error occurred: #{e.message}"\nend