# Using ActiveRecord example\ndef get_subcategories(category_id)\n  Category.joins(:category_closures_as_descendant).where('category_closure.ancestor = ?', category_id)\nend\n\ndef get_path_to_root(category_id)\n  Category.joins(:category_closures_as_ancestor)\n          .select('ancestor AS parent_id, categories.title')\n          .where('category_closure.descendant = ?', category_id)\n          .order('category_closure.depth DESC')\nend