class User < ApplicationRecord\n  enum status: { invited: 0, registered: 1 }\n\n  # Validations for an invited user\n  validates :name, presence: true, if: :invited?\n  validates :email, presence: true, format: { with: URI::MailTo::EMAIL_REGEXP }, if: :invited?\n\n  # Validations for a registered user (after accepting invitation)\n  validates :password, presence: true, length: { minimum: 8 }, if: :registered?\nend