current_time = Time.current  # This respects the config.time_zone setting\n   one_second_ago = 1.second.ago  # Uses ActiveSupport to calculate the correct time, respecting time zones\n\n   puts "Current Time: #{current_time}"\n   puts "One Second Ago: #{one_second_ago}"\n\n   # Compare plain Ruby Time.new vs Rails Time.current\n   plain_time_new = Time.new\n   rails_time_current = Time.current\n\n   puts "Plain Ruby Time.new: #{plain_time_new}"\n   puts "Rails Time.current: #{rails_time_current}"