require 'htmlentities'\n\ncoder = HTMLEntities.new\n\n# Escaping\nescaped = coder.escape("…")\nputs escaped  # => "&#8230;"\n\n# Unescaping\nunescaped_numeric = coder.decode('&#8230;')\nputs unescaped_numeric  # => "…"\n\nunescaped_named = coder.decode('&hellip;')\nputs unescaped_named  # => "…"\n\nunescaped_cent_numeric = coder.decode('&#162;')\nputs unescaped_cent_numeric  # => "¢"\n\nunescaped_cent_named = coder.decode('&cent;')\nputs unescaped_cent_named  # => "¢"