require 'rails_helper'\n\nRSpec.describe Game, type: :model do\n  let(:player1) { players(:one) }\n  let(:cards_b_and_c) { [Card.find_by(name: 'B'), Card.find_by(name: 'C')] } # Adjust the query to match your card model\n  let(:game_four) { games(:four) }\n\n  before do\n    game_four.players << player1\n    player1.hand.add_cards(cards_b_and_c)\n    game_four.start\n  end\n\n  it "should set up game correctly with specific cards for player 1" do\n    expect(player1.hand.cards).to match_array(cards_b_and_c)\n    expect(game_four.status).to eq('started')\n  end\nend