# Routes for bananas\nresources :bananas, only: [:index, :show] do\n  member do\n    get 'peel'\n    get 'length'\n    # Add other sub-characteristics here\n  end\nend\n\n# Corresponding controller actions\nclass BananasController < ApplicationController\n  before_action :find_banana_by_name_or_number\n\n  def index\n    @bananas = Banana.limit(10)\n    render json: @bananas\n  end\n\n  def show\n    render json: @banana\n  end\n\n  def peel\n    render json: { peel: @banana.peel_data }\n  end\n\n  def length\n    render json: { length: @banana.length_data }\n  end\n\n  private\n\n  def find_banana_by_name_or_number\n    if params[:id] =~ /\D/  # Assuming name contains non-digit characters\n      @banana = Banana.find_by(name: params[:id])\n    else\n      @banana = Banana.find_by(number: params[:id])\n    end\n    render json: { error: 'Banana not found' }, status: :not_found unless @banana\n  end\nend