class User < ApplicationRecord\n  # Automatically track previous value of email\n  include ActiveModel::Dirty\n\n  after_save :sync_with_chimp_if_email_changed\n\n  private\n\n  def sync_with_chimp_if_email_changed\n    if email_changed?\n      update_mailchimp_api(email)\n    end\n  end\n\n  def update_mailchimp_api(new_email)\n    # Implement your logic to update Mailchimp here using new_email\n    # Example: mailchimp_client.update_user_email(id, new_email)\n  end\nend