def update\n  @album = Album.find(params[:id])\n  if @album.update(album_params)\n    redirect_to(:action => 'list')\n  else\n    render(:action => 'edit')\n  end  \nend\n\nprivate\n\ndef album_params\n  params.require(:album).permit(:attribute1, :attribute2) # Permit only the attributes you want to allow\nend