class Concept < ApplicationRecord\n  before_save :set_status\n\n  # Your other model code...\n\n  private\n\n  def set_status\n    if status.blank?\n      case [consulted_legal, consulted_marketing]\n      when [true, true]\n        self.status = "Pending Approval"\n      when [true, false]\n        self.status = "Awaiting Marketing Approval"\n      when [false, true]\n        self.status = "Awaiting Legal Approval"\n      when [false, false]\n        self.status = "Awaiting Marketing & Legal Approval"\n      end\n    end\n  end\nend