class YourResourcesController < ApplicationController\n  # ...\n\n  def revert_to_version\n    @resource = YourResource.find(params[:id])\n    version_number = params[:version_number].to_i\n\n    # Find the right version and revert to it\n    version = @resource.versions.find_by(version: version_number)\n    if version.nil?\n      flash[:alert] = "Version #{version_number} does not exist."\n      redirect_to edit_your_resource_path(@resource) and return\n    end\n\n    # Revert and save the record\n    version.revert_to!\n    @resource.reload\n\n    flash[:notice] = "Successfully reverted #{@resource.name} to version #{version_number}."\n    redirect_to your_resource_path(@resource)\n  rescue => e\n    flash[:alert] = "Failed to revert #{@resource.name}: #{e.message}"\n    redirect_to edit_your_resource_path(@resource) and return\n  end\n\n  # ...\nend