def create\n  @image = Image.new(image_params)\n\n  if @image.save \n    # Add some logging to verify the image id\n    Rails.logger.debug("Image saved with ID: #{@image.id}")\n    redirect_to image_url(@image)\n  else\n    flash.now[:notice] = 'Your image did not pass validation.'\n    render :action => 'new'\n  end\nend\n\nprivate\n\ndef image_params\n  params.require(:image).permit(:filename, :description, :image_file, :image_file_url, :image_file_temp)\nend